import React, { useState, useContext } from 'react';
import {
    StyleSheet, View, Image, Text, Platform,
    TouchableOpacity, TouchableWithoutFeedback, Modal, Pressable,
} from 'react-native';

import { AuthContext } from '../components/AuthProvider';
import Styles from '../styles/Styles';

export default function Header() {
    const { userInfo, logout } = useContext(AuthContext);
    const [isModalVisible, setModalVisible] = useState(false);

    const toggleModal = () => { setModalVisible(!isModalVisible); };
    const handleLogout = () => { setModalVisible(false); logout(); };
    const handleBackgroundPress = () => { if (isModalVisible) toggleModal(); }

    return (
        <View style={styles.headerContainer}>
            {/* <Text>{userInfo.firstName}! Your OS is {Platform.OS} </Text> */}
            <Pressable onPress={toggleModal} >
                <Image
                    source={require('../assets/icon.png')}
                    style={styles.profileImage}
                />
            </Pressable>
            <Modal
                animationType="none"
                transparent={true}
                visible={isModalVisible}
                onRequestClose={toggleModal}
            >
                <TouchableWithoutFeedback onPress={handleBackgroundPress}>
                    <View style={styles.modalContainer}>
                        <View style={Styles.dropdownContent}>
                            <TouchableOpacity style={Styles.dropdownItem} onPress={handleLogout}>
                                <Text>Logout</Text>
                            </TouchableOpacity>
                            {/* Add more items as needed */}
                        </View>
                    </View>
                </TouchableWithoutFeedback>
            </Modal>
        </View>
    );
}

const styles = StyleSheet.create({
    headerContainer: {
        minWidth: '100%',
        height: 70,
        paddingHorizontal: 20,
        alignItems: 'left',
        justifyContent: 'center',
    },
    profileImage: {
        width: 40,
        height: 40,
        borderRadius: 20,
        alignSelf: 'flex-end',
    },
    modalContainer: {
        flex: 1,
        justifyContent: 'flex-start',
        alignItems: 'flex-end',
    },
});
