import React, { useState, useContext } from 'react';
import {
    StyleSheet, View, Image, Text, Platform,
    TouchableOpacity, TouchableWithoutFeedback, Modal,
} from 'react-native';

import { AuthContext } from '../components/AuthProvider';
import Button from '../components/Button';

export default function Header() {
    const { userInfo, logout } = useContext(AuthContext);
    const [isModalVisible, setModalVisible] = useState(false);

    const toggleModal = () => { setModalVisible(!isModalVisible); };
    const handleLogout = () => { setModalVisible(false); logout(); };
    const handleBackgroundPress = () => { if (isModalVisible) toggleModal(); }

    return (
        <View style={styles.headerContainer}>
            <Text>{userInfo.firstName}! Your OS is {Platform.OS} </Text>
            <TouchableOpacity onPress={toggleModal}>
                <Image
                    source={require('../assets/icon.png')}
                    style={styles.profileImage}
                />
            </TouchableOpacity>
            <Modal
                animationType="none"
                transparent={true}
                visible={isModalVisible}
                onRequestClose={toggleModal}
            >
                <TouchableWithoutFeedback onPress={handleBackgroundPress}>
                    <View style={styles.modalContainer}>
                        <View style={styles.modalContent}>
                            <TouchableOpacity style={styles.modalItem} onPress={handleLogout}>
                                <Text>Logout</Text>
                            </TouchableOpacity>
                            {/* Add more items as needed */}
                        </View>
                    </View>
                </TouchableWithoutFeedback>
            </Modal>
        </View>
    );
}

const styles = StyleSheet.create({
    headerContainer: {
        minWidth: '100%',
        height: 70,
        paddingHorizontal: 20,
        alignItems: 'left',
        justifyContent: 'center',
        backgroundColor: '#007800',

    },
    profileImage: {
        width: 40,
        height: 40,
        borderRadius: 20,
        alignSelf: 'flex-end',
    },
    modalContainer: {
        flex: 1,
        justifyContent: 'flex-start',
        alignItems: 'flex-end',
        // backgroundColor: 'rgba(0, 0, 0, 0.5)',
    },
    modalContent: {
        width: 150, // Adjust the width as needed
        marginTop: 70, // Adjust the marginTop to position it below the profile image
        marginRight: 10,
        backgroundColor: '#fff',
        borderRadius: 5,
        overflow: 'hidden',
    },
    modalItem: {
        padding: 20,
        borderBottomWidth: 1,
        borderBottomColor: '#ccc',
        width: '100%',
        alignItems: 'center',
    },
});



