import AsyncStorage from '@react-native-async-storage/async-storage';
import axios from 'axios';
import React, { createContext, useEffect, useState } from 'react';
import * as URL from '../config';
import BubbleApi from '../api/BubbleApi';

export const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
    const [userInfo, setUserInfo] = useState({});
    const [isLoading, setIsLoading] = useState(false);
    const [splashLoading, setSplashLoading] = useState(false);

    const api = new BubbleApi();

    const login = async (email, password) => {
        setIsLoading(true);

        let loginUser = await api.apiLogin(email, password);

        if (loginUser && loginUser.userID) {
            setUserInfo(loginUser);
            await AsyncStorage.setItem('userInfo', JSON.stringify(loginUser));

            const userDetails = await api.fetchUser(loginUser.userID);
            loginUser = { ...loginUser, ...userDetails };

            setUserInfo(loginUser);
            AsyncStorage.setItem('userInfo', JSON.stringify(loginUser));

            console.log('fetched', loginUser);
        } else {
            alert('Email / password incorrect');
        }

        setIsLoading(false);
    }

    const logout = async () => {
        setIsLoading(true);

        try {
            api.printContents();
            const logoutResult = await api.apiLogout();

            console.log(logoutResult);
            AsyncStorage.removeItem('userInfo');
            setUserInfo({});
            setIsLoading(false);
        } catch (err) {
            console.log(`logout error ${e}`);
            setIsLoading(false);
        };
    };

    const isLoggedIn = async () => {
        try {
            setSplashLoading(true);

            const userInfoJSON = await AsyncStorage.getItem('userInfo');

            if (userInfoJSON && userInfoJSON !== "undefined") {
                console.log(userInfoJSON);
                userInfo = JSON.parse(userInfoJSON);
                setUserInfo(userInfo);
                console.log(`found user ${userInfo.name} in local cache`);
            }

            setSplashLoading(false);
        } catch (err) {
            setSplashLoading(false);
            console.log(`isLoggedIn error ${err}`);
        }
    };

    useEffect(() => {
        isLoggedIn();
    }, []);

    return (
        <AuthContext.Provider
            value={{
                isLoading,
                userInfo,
                splashLoading,
                login,
                logout,
            }}>
            {children}
        </AuthContext.Provider>
    );
};
