import { StyleSheet, View } from 'react-native';
import Button from './Button';

export default function ButtonsFwdBack({ gameState, setGameState, setPhase }) {
    const { phase, scoutSelectionValid: clickable } = gameState;

    return (
        <View style={styles.fwdBackContainer}>
            {phase.backLabel && 
                showButton(phase.backLabel, phase.backActions, "back", clickable, setPhase)
            }
            {phase.forwardLabel && 
                showButton(phase.forwardLabel, phase.forward, phase.forwardActions, "forward", clickable, setPhase)
            }
        </View>
    );
}

function showButton(label, newPhase, actions, theme, clickable, setPhase) {
    const onPress = () => setPhase(newPhase);

    return (
        <Button theme={theme} label={label} onPress={onPress} clickable={clickable} />
    );
}

const styles = StyleSheet.create({
    fwdBackContainer: {
        minWidth: '100%',
        height: 40,
        paddingLeft: 10,
        paddingRight: 10,
        marginTop: 20,
        justifyContent: 'center',   // vertical center
        flexDirection: 'row',       // place buttons in a row
    },
})
