import { StyleSheet, Text, View, TextInput, Image } from 'react-native';
import AsyncStorage from '@react-native-async-storage/async-storage';

import React, { useContext, useEffect } from 'react';
import Button from '../components/Button';
import BubbleApi from '../api/BubbleApi';

import Styles from '../styles/Styles';
import { AuthContext } from '../components/AuthProvider';
import BlueAllianceApi from '../api/BlueAllianceApi';

export default function ScoutPage() {
    const { userInfo, logout, appVariables, setAppVariables } = useContext(AuthContext);

    const hasAppVariables = async () => {
        try {
            const appVariablesJSON = await AsyncStorage.getItem('appVariables');

            if (appVariablesJSON && appVariablesJSON !== "undefined") {
                const retrievedAppVariables = JSON.parse(appVariablesJSON);
                if (retrievedAppVariables) {  // todo: check for current
                    setAppVariables(retrievedAppVariables);
                    console.log('found appVariables', retrievedAppVariables, 'in local cache');
                }
            } else {
                console.log('no cached appVariables found')
            }
        } catch (err) {
            console.log(`hasAppVariables error ${err}`);
        }
    };

    useEffect(() => {
        const loadEvent = async () => {
            await hasAppVariables();
            try {
                if (!appVariables || !appVariables.fetchedDate) {
                    const fetchedAppVariables = await BubbleApi.getAppVariables();
                    console.log('fetched', fetchedAppVariables, 'to store to local');

                    setAppVariables(fetchedAppVariables);   // store in context
                    await AsyncStorage.setItem('appVariables', JSON.stringify(fetchedAppVariables));
                } 
                console.log({appVariables});
                const colorDict = await BubbleApi.getColorDict(userInfo.team);
                const matches = await BlueAllianceApi.fetchMatches(appVariables.eventKey);
                console.log({ fetchedAppVariables, colorDict, matches });
            } catch (err) {
                console.error("loadEvent failed", err);
            }
        }

        loadEvent();
    }, []);

    return (
        <View style={Styles.column30}>
            <Image style={styles.image} source={require("../assets/loginlogo.jpg")} />
            <View id="textBlock">
                <View style={Styles.groupLeft}>
                    <Text style={[Styles.mediumTitle, styles.loginTitle]}>Welcome {userInfo.name}</Text>
                    <Button label="Log out" theme="primary" onPress={logout} />
                </View>
            </View>
        </View>
    );
}

const styles = StyleSheet.create({
    bottomNotes: {
        marginTop: 60,
        marginBottom: 100,
    },
    loginTitle: {
        marginTop: 30,
        marginBottom: 12,
    },
    image: {
        width: 192,
        height: 51.5,
        alignSelf: 'center',
        marginBottom: 30,
        marginTop: 100,
    },
});
