import { StyleSheet, Text, View, TextInput, Image } from 'react-native';
import AsyncStorage from '@react-native-async-storage/async-storage';

import React, { useContext, useEffect } from 'react';
import Button from '../components/Button';
import BubbleApi from '../api/BubbleApi';

import Styles from '../styles/Styles';
import { LOCALKEYS } from '../config';
import { AuthContext } from '../components/AuthProvider';
import BlueAllianceApi from '../api/BlueAllianceApi';

export default function ScoutPage() {
    const {
        userInfo, logout,
        appVariables, setAppVariables,
        eventMatches, setEventMatches,
        colorDict, setColorDict,
        lastChanged, setLastChanged,
    } = useContext(AuthContext);

    // get lastChanged
    useEffect(() => {
        const populateLastChanged = async () => {
            const apiLastChanged = await BubbleApi.apiGetLastChanged();
            setLastChanged(apiLastChanged);

            console.log('last changed loaded from api');
        };

        populateLastChanged();
    }, []); // Empty dependency array to run only on mount

    // get appVariables from cache or api
    useEffect(() => {
        const populateAppVariables = async () => {
            // Check if appVariables is in AsyncStorage
            const storedAppVariables = await AsyncStorage.getItem(LOCALKEYS.APPVAR);

            if (storedAppVariables) {
                // If it exists, set it in the state
                const parsedAppVariables = JSON.parse(storedAppVariables);
                parsedAppVariables.fetchedDate = new Date(parsedAppVariables.fetchedDate);
                setAppVariables(parsedAppVariables);
                const { fetchedDate } = parsedAppVariables;
                console.log('app variables found in cache', parsedAppVariables);
                console.log('lastchanged is', lastChanged);
                console.log('fetchedDate is', fetchedDate);
                if (fetchedDate) console.log(typeof fetchedDate);
                console.log('lastchanged appVariables is', typeof lastChanged.appVariables);
            } else {
                // If not, fetch it from the API
                const apiAppVariables = await BubbleApi.apiGetAppVariables();
                setAppVariables(apiAppVariables);

                // Store it in AsyncStorage for future use
                await AsyncStorage.setItem('appVariables', JSON.stringify(apiAppVariables));
                console.log('app variables loaded from api');
            }
        };

        populateAppVariables();
    }, [lastChanged]); // Empty dependency array to run only on mount

    // get eventMatches from cache or API - depends on appVariables
    useEffect(() => {
        const populateMatches = async () => {
            if (!appVariables || !appVariables.eventKey) return;

            const storedEventMatches = await AsyncStorage.getItem('eventMatches');
            if (storedEventMatches) {
                // If it exists, set it in the state
                const parsedEventMatches = JSON.parse(storedEventMatches);
                if (parsedEventMatches.eventKey === appVariables.eventKey) {
                    setEventMatches(parsedEventMatches);
                    console.log('event matches found in cache');
                    return;
                }
                // if the event key mismatches, then ignore the cache
            }

            // If not, fetch it from the API
            const apiEventMatches = await BlueAllianceApi.fetchMatches(appVariables.eventKey);
            setEventMatches(apiEventMatches);

            // Store it in AsyncStorage for future use
            await AsyncStorage.setItem('eventMatches', JSON.stringify(apiEventMatches));
            console.log('event matches loaded from api');
        };

        populateMatches();
    }, [appVariables]); // Trigger only when appVariables changes

    // get colorDict from cache or API - depends on userInfo
    useEffect(() => {
        const populateColorDict = async () => {
            if (!userInfo) return;

            const storedColorDict = await AsyncStorage.getItem('colorDict');
            if (storedColorDict) {
                // If it exists, set it in the state
                const parsedColorDict = JSON.parse(storedColorDict);

                if (parsedColorDict.teamNumT === userInfo.team) {
                    setColorDict(parsedColorDict);
                    console.log('color dict found in cache');
                    return;
                }
            }

            // If not, fetch it from the API
            const apiColorDict = await BubbleApi.apiGetColorDict(userInfo.team);
            setColorDict(apiColorDict);

            // Store it in AsyncStorage for future use
            await AsyncStorage.setItem('colorDict', JSON.stringify(apiColorDict));
            console.log('color dict loaded from api');

        };

        populateColorDict();
    }, [userInfo]); // Trigger only when appVariables changes

    // const colorDict = await BubbleApi.getColorDict(userInfo.team);

    return (
        <View style={Styles.column30}>
            <Image style={styles.image} source={require("../assets/loginlogo.jpg")} />
            <View id="textBlock">
                <View style={Styles.groupLeft}>
                    <Text style={[Styles.mediumTitle, styles.loginTitle]}>Welcome {userInfo.name}</Text>
                    <Button label="Log out" theme="primary" onPress={logout} />
                </View>
            </View>
        </View>
    );
}

const styles = StyleSheet.create({
    bottomNotes: {
        marginTop: 60,
        marginBottom: 100,
    },
    loginTitle: {
        marginTop: 30,
        marginBottom: 12,
    },
    image: {
        width: 192,
        height: 51.5,
        alignSelf: 'center',
        marginBottom: 30,
        marginTop: 100,
    },
});
